    // MARK: Tableview Data Source Methods
    
    
    // Number of Sections in table
    func numberOfSections(in tableView: UITableView) -> Int {
        return 1
    }
    // Number of Rows in table
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return countryNames.count
    }
    
    // Find and insert data from model in row for showing
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "CountryName")
        let countryFormRow = countryNames[(indexPath as NSIndexPath).row]
        cell?.textLabel?.text = countryFormRow
        return cell!
    }

